mapper.insert:
insert into pltf_sap_conn_resource (type, name, description, app_server, instance_no, system_id, router_string, msg_server, router, grp_server, client, username, password, pool_capacity, peak_limit, fg_use, fg_delete, reg_username, reg_date) values (
	#{type}, #{name}, #{description}, #{appServer}, #{instanceNo}, #{systemId}, #{routerString}, #{msgServer}, #{router}, #{grpServer}, #{client}, #{username}, #{password}, #{poolCapacity}, #{peakLimit}, 'N', 'N', #{regUsername}, #{regDate}
)

mapper.update:
update pltf_sap_conn_resource set
	fg_use=#{fgUse}
where
	scr_seq=#{scrSeq}
