mapper.insert:
insert into pltf_citem (p_seq, type, name, description, component, entry_point, parameter, sort, fg_popup, fg_visible, fg_delete, from_valid, to_valid, reg_username, reg_date) values (
	#{pSeq}, #{type}, #{name}, #{description}, #{component}, #{entryPoint}, #{parameter}, #{sort}, #{fgPopup}, #{fgVisible}, #{fgDelete}, #{fromValid}, #{toValid}, #{regUsername}, #{regDate}
)

insert into pltf_citem_user_rl (c_seq, u_seq, fg_delete, reg_username, reg_date) values (
	#{cSeq}, #{uSeq}, #{fgDelete}, #{regUsername}, #{regDate}
)

insert into pltf_citem_lang_rl (c_seq, l_seq, reg_username, reg_date) values (
	#{cSeq}, #{lSeq}, #{regUsername}, #{regDate}
)

mapper.update:
update pltf_citem set
	fg_delete='Y',
	mod_username=#{modUsername},
	mod_date=#{modDate}
where
	c_seq=#{cSeq}

update pltf_citem_user_rl set
	fg_delete='Y',
	mod_username=#{modUsername},
	mod_date=#{modDate}
where
	u_seq=#{uSeq}

mapper.select:
select
*
from
pltf_citem



		c_seq=#{cSeq}



			name=#{name}




			type=#{type}




			fg_delete=#{fgDelete}

select a.* from pltf_citem a, pltf_citem_user_rl b where a.c_seq=b.c_seq and a.fg_delete='N' and a.from_valid < now() and a.to_valid >= now() and b.fg_delete='N'
and a.p_seq=#{pSeq}
and a.fg_visible='Y'
and b.u_seq=#{uSeq}
and b.u_seq=(select u_seq from pltf_user_account where username=#{username})
and a.name=#{name}
and a.type=#{type}
a.type=#{type}
order by a.sort, a.c_seq

	select
	a.*
	from
	pltf_citem a
	where
	a.fg_delete='N'





					a.c_seq=#{cSeq}






		and a.c_seq=#{cSeq}



		and a.p_seq=#{pSeq}



		and a.name=#{name}



		and type=#{type}






					a.type=#{type}






    	and a.fg_visible='Y'
    	and a.from_valid < now() and a.to_valid >= now()


    order by a.sort, a.c_seq

select a.* from pltf_citem a where a.fg_delete='N'
a.name like concat('%', upper(trim(#{query})), '%') or upper(a.description) like concat('%', upper(trim(#{query})), '%')
or a.c_seq=#{cSeq}
and type=#{type}
a.type=#{type}

select b.* from pltf_citem a, pltf_citem_lang_rl b, pltf_lang_detail c, pltf_lang_kind d where a.c_seq=#{cSeq}
and b.l_seq=#{lSeq}
and a.fg_delete='N' and a.c_seq=b.c_seq and b.l_seq=c.l_seq and c.fg_delete='N' and c.lk_seq=d.lk_seq
and d.basename=#{basename}
and d.fg_delete='N' order by d.fg_default desc
