mapper.insert:
insert into pltf_lang(basename, type, seq, fg_delete, reg_username, reg_date) values (
	#{basename}, #{type}, upper(#{seq}), #{fgDelete}, #{regUsername}, #{regDate}
)

insert into pltf_lang_detail(l_seq, lk_seq, text, fg_delete, reg_username, reg_date) values (
	#{lSeq}, #{lkSeq}, #{text}, #{fgDelete}, #{regUsername}, #{regDate}
)

mapper.update:
update pltf_lang set 
	basename=#{basename}, type=#{type}, mod_username=#{modUsername}, mod_date=#{modDate}
where
	l_seq=#{lSeq}

update pltf_lang_detail set
	mod_username=#{modUsername},
	mod_date=#{modDate}


		,l_seq=#{lSeq}


		,lk_seq=#{lkSeq}


		,text=#{text}

where


			ld_seq=#{ldSeq}


			and l_seq=#{lSeq} 


			and lk_seq=#{lkSeq}

update pltf_lang set fg_delete='Y', mod_username=#{modUsername}, mod_date=#{modDate}
where		


		l_seq=#{lSeq}

update pltf_lang_detail set fg_delete='Y', mod_username=#{modUsername}, mod_date=#{modDate}
where		


		l_seq=#{lSeq}

mapper.select:
select
a.*
from
pltf_lang a		
where
fg_delete='N'


	and a.l_seq=#{lSeq}



	and a.basename=#{basename}



	and a.type=#{type}



	and a.seq=#{seq}

select a.* from pltf_lang a where not exists ( select 1 from pltf_citem_lang_rl where l_seq=a.l_seq ) and a.fg_delete='N'
and a.basename=#{basename}
and a.type=#{type}
and ( exists ( select 1 from pltf_lang_detail where l_seq=a.l_seq and fg_delete='N' and upper(text) like concat('%', upper(#{query}), '%') ) or concat(a.basename, '_', a.type, '_', a.seq) like concat('%', upper(#{query}), '%') or a.seq like concat('%', upper(#{query}), '%') )
order by a.l_seq desc

select * from pltf_lang_kind where fg_delete='N'
and basename=#{basename}
order by fg_default desc, sort

select 
*
from
pltf_lang_detail




			l_seq=#{lSeq}


			and lk_seq=#{lkSeq}

select
b.*
from
pltf_lang a,
pltf_lang_detail b,
pltf_lang_kind c
where
a.basename=#{basename}
and a.type=#{type}
and a.seq=#{seq}
and a.fg_delete='N'
and a.l_seq=b.l_seq
and b.fg_delete='N'		
and b.lk_seq=c.lk_seq
and c.basename=#{basename}


	and c.code=#{code}
