mapper.select:
select
a.*
from
pltf_credentials_policy a
where
a.fg_delete='N'


	and a.name=#{name}



	and a.fg_use=#{fgUse}



	and a.fg_default=#{fgDefault}


order by a.cp_seq

select a.* from pltf_credentials_policy a where a.fg_delete='N'
and (upper(a.name) like concat('%', upper(trim(#{query})), '%') or upper(a.name) like concat('%', upper(trim(#{query})), '%'))
and a.fg_use=#{fgUse}
and a.fg_default=#{fgDefault}
