mapper.insert:

	select pltf_ufl_seq.nextval from dual


insert into pltf_upload_file_list (ufl_seq, uf_seq, t_seq, origin_name, saved_name, extension, size, fg_delete, reg_username, reg_date) values (
	#{uflSeq}, #{ufSeq}, #{tSeq}, #{originName}, #{savedName}, #{extension}, #{size}, #{fgDelete}, #{regUsername}, #{regDate}
)

mapper.update:
update pltf_upload_file_list set
	fg_delete='Y',
	mod_username=#{modUsername},
	mod_date=#{modDate}
where
	ufl_seq=#{uflSeq}
