mapper.insert:
insert into pltf_user_group (name, description, scr_seq, date_format1, time_format1, date_format2, time_format2, country, timezone, lang_kind, def_lang, cp_seq, fg_delete, reg_username, reg_date) values (
	#{name}, #{description}, #{sapConnResource.scrSeq}, #{dateFormat1}, #{timeFormat1}, #{dateFormat2}, #{timeFormat2}, #{country}, #{timezone}, #{langKind}, #{defLang}, #{credentialsPolicy.cpSeq}, 'N', #{regUsername}, #{regDate}
)

mapper.update:
update pltf_user_group set
	fg_delete=#{fgDefault},
	mod_username=#{modUsername},
	mod_date=#{modDate}
where
	ug_seq=#{ugSeq}
