mapper.insert:

	select pltf_c_seq.nextval from dual


insert into pltf_citem (c_seq, p_seq, type, name, description, component, entry_point, parameter, sort, fg_visible, launch_type, scr_width, scr_height, fg_delete, from_valid, to_valid, reg_username, reg_date) values (
	#{cseq}, #{pseq}, #{type}, #{name}, #{description}, #{component}, #{entryPoint}, #{parameter}, #{sort}, #{fgVisible}, #{launchType}, #{scrWidth}, #{scrHeight}, #{fgDelete}, #{fromValid}, #{toValid}, #{regUsername}, #{regDate}
)


	select pltf_cur_seq.nextval from dual


insert into pltf_citem_user_rl (cur_seq, c_seq, u_seq, fg_delete, reg_username, reg_date) values (
	#{curSeq}, #{cseq}, #{useq}, #{fgDelete}, #{regUsername}, #{regDate}
)


	select pltf_clr_seq.nextval from dual


insert into pltf_citem_lang_rl (clr_seq, c_seq, l_seq, reg_username, reg_date) values (
	#{clrSeq}, #{cseq}, #{lseq}, #{regUsername}, #{regDate}
)

mapper.update:
update pltf_citem set
	fg_delete='Y',
	mod_username=#{modUsername},
	mod_date=#{modDate}
where
	c_seq=#{cseq}

update pltf_citem_user_rl set
	fg_delete='Y',
	mod_username=#{modUsername},
	mod_date=#{modDate}
where
	u_seq=#{useq}

mapper.select:
select
*
from
pltf_citem



		c_seq=#{cseq}



			name=#{name}




			type=#{type}




			fg_delete=#{fgDelete}

select
a.*
from
pltf_citem a,
pltf_citem_user_rl b
where
a.c_seq=b.c_seq
and a.fg_delete='N'		
and a.from_valid < sysdate and a.to_valid >= sysdate
and b.fg_delete='N'


	and a.p_seq=#{pseq}




	and a.fg_visible='Y'



	and b.u_seq=#{useq}



	and b.u_seq=(select u_seq from pltf_user_account where username=#{username})



	and a.name=#{name}






				a.name=#{name}






	and a.type=#{type}






				a.type=#{type}





order by a.sort, a.c_seq

	select
	a.*
	from
	pltf_citem a
	where
	a.fg_delete='N'





					a.c_seq=#{cseq}






		and a.c_seq=#{cseq}



		and a.p_seq=#{pseq}



		and a.name=#{name}



		and type=#{type}






					a.type=#{type}






    	and a.fg_visible='Y'
    	and a.from_valid < sysdate and a.to_valid >= sysdate


    order by a.sort, a.c_seq

select a.* from pltf_citem a where a.fg_delete='N'
a.name like '%'||upper(trim(#{query}))||'%' or upper(a.description) like '%'||upper(trim(#{query}))||'%'
or a.c_seq=#{cseq}
and type=#{type}
a.type=#{type}
order by a.c_seq desc

select b.* from pltf_citem a, pltf_citem_lang_rl b, pltf_lang c, pltf_lang_detail d, pltf_lang_kind e where a.c_seq=#{cseq}
and b.l_seq=#{lseq}
and a.fg_delete='N' and a.c_seq=b.c_seq and b.l_seq=c.l_seq and c.fg_delete='N' and c.l_seq=d.l_seq and d.fg_delete='N' and d.lk_seq=e.lk_seq
and e.basename=#{basename}
and e.fg_delete='N' order by e.fg_default desc
