mapper.insert:

	select ep_si_seq.nextval from dual


insert into ep_survey_item (si_seq, sq_seq, title, fg_delete, reg_username, reg_date) values (
	#{siSeq}, #{sqSeq}, #{title}, #{fgDelete}, #{regUsername}, #{regDate}
)

mapper.update:
update ep_survey_item set
	title=#{title},
	mod_username=#{modUsername},
	mod_date=#{modDate}
where
	si_seq=#{siSeq}

update ep_survey_item set
	fg_delete='Y',
	mod_username=#{modUsername},
	mod_date=#{modDate}
where
	si_seq=#{siSeq}
