mapper.insert:

	select ep_sq_seq.nextval from dual


insert into ep_survey_question (sq_seq, s_seq, title, type, mandatory_cnt, fg_mandatory, fg_delete, reg_username, reg_date) values (
	#{sqSeq}, #{sSeq}, #{title}, #{type}, #{mandatoryCnt}, #{fgMandatory}, #{fgDelete}, #{regUsername}, #{regDate}
)

mapper.update:
update ep_survey_question set
	fg_delete='Y',
	mod_username=#{modUsername},
	mod_date=#{modDate}
where 
	sq_seq=#{sqSeq}
