mapper.insert:

	select ep_st_seq.nextval from dual


insert into ep_survey_target (st_seq, s_seq, type, target, target_name, fg_delete, reg_username, reg_date) values (
	#{stSeq}, #{sSeq}, #{type}, #{target}, #{targetName}, #{fgDelete}, #{regUsername}, #{regDate}
)

mapper.update:
update ep_survey_target set
	fg_delete='Y',
	mod_username=#{modUsername},
	mod_date=#{modDate}
where
	st_seq=#{stSeq}
